import React from "react";
import "./NavBar.css";

interface EnergyCostProps {
  distance: number;
  showDistance: boolean;
}

export const EnergyCost: React.FC<EnergyCostProps> = (props) => {
  return (
    <div
      className={
        "distance " + (showDistance ? "show-distance" : "hide-distance")
      }
    >
      <span>The energy cost of the path is {props.distance}</span>
    </div>
  );
};
