import React, { useState } from "react";
import { WrapperCSSTransition } from "../Wrapper/Wrapper";
import "./Tutorial.css";

interface TutorialProps {}

export const Tutorial: React.FC<TutorialProps> = (props) => {
  const [activePage, setActivePage] = useState("first-page");
  return (
    <div className="tutorial-panel">
      <WrapperCSSTransition
        in={activePage === "first-page"}
        unmountOnExit
        timeout={500}
        classNames="page-container"
        divClassName={"first-page page"}
        handleEnter={() => {}}
      >
        <h1 className="big-title">
          Welcome to the "Rat in a maze" Pathfinding Visulazing tool!
        </h1>
        <div className="emoji-container">
          <span role="img" aria-label="rat" className="tutorial-emoji">
            üêÄ
          </span>
          <span role="img" aria-label="cheese" className="tutorial-emoji">
            üßÄ
          </span>
        </div>
        <span>
          <h3 className="text">
            This is a tool I built to learn more about graph algorithm, and
            particularly Pathfinding Algorithms. There are some cool features in
            this tool.
          </h3>
          <h2>So without further ado, let's dive into how it works!</h2>
        </span>
        <button
          className="button button-next"
          onClick={() => setActivePage("second-page")}
        >
          Next {">"}
        </button>
      </WrapperCSSTransition>
      <WrapperCSSTransition
        in={activePage === "second-page"}
        unmountOnExit
        timeout={500}
        classNames="page-container"
        divClassName={"first-page page"}
        handleEnter={() => {}}
      >
        <div className="second-page page">
          <h1 className="big-title">The Maze.</h1>

          <h3 className="text">
            The maze is composed of squares. Let's imagine the rat in the maze
            is hungry, and it wants to eat all the cheese in the maze.
          </h3>
          <button
            className="button button-prev"
            onClick={() => setActivePage("first-page")}
          >
            {"<"} Previous
          </button>
          <button
            className="button button-next"
            onClick={() => setActivePage("third-page")}
          >
            Next {">"}
          </button>
        </div>
      </WrapperCSSTransition>
      <WrapperCSSTransition
        in={activePage === "third-page"}
        unmountOnExit
        timeout={500}
        classNames="page-container"
        divClassName={"first-page page"}
        handleEnter={() => {}}
      >
        <div className="second-page page">
          <h1 className="big-title">The Maze.</h1>

          <h3 className="text">
            The maze is composed of squares. Let's imagine the rat in the maze
            is hungry, and it wants to eat all the cheese in the maze.
          </h3>
          <button
            className="button-prev"
            onClick={() => setActivePage("first-page")}
          >
            {"<"} Previous
          </button>
          <button
            className="button-next"
            onClick={() => setActivePage("third-page")}
          >
            Next {">"}
          </button>
        </div>
      </WrapperCSSTransition>
    </div>
  );
};
