import React from "react";
import "./NavBar.css";

interface EnergyCostProps {
  distance: number;
}

export const EnergyCost: React.FC<EnergyCostProps> = (props) => {
  return (
    <div
      className={
        "distance " + (props.distance > -2 ? "show-distance" : "hide-distance")
      }
    >
      {props.distance > -2 && <span>The energy cost is: {props.distance}</span>}
    </div>
  );
};
