import React, { useState, useEffect } from "react";
import { CSSTransition } from "react-transition-group";
import "./NavBar.css";

export const NavBar: React.FC = (props) => {
  return (
    <nav className="navbar">
      <ul className="navbar-nav">{props.children}</ul>
    </nav>
  );
};

interface NavChangingButtonItemProps {
  isVisualized: boolean;
  className: string;
  visualizedClassName: string;
  handleClick: () => void;
}

export const NavChangingButtonItem: React.FC<NavChangingButtonItemProps> = (
  props
) => {
  return (
    <li
      className={
        "nav-item " +
        props.className +
        (props.isVisualized ? " " + props.visualizedClassName : "")
      }
      onClick={(e) => {
        props.handleClick();
      }}
    >
      <p>{props.children}</p>
    </li>
  );
};

interface NavDropDownItemProps {
  icon: string;
  text: string;
  className: string;
  isVisualized: boolean;
  shouldGreyOut: boolean;
}

export const NavDropDownItem: React.FC<NavDropDownItemProps> = (props) => {
  const [open, setOpen] = useState(false);

  const handleWindowClick: (e: any) => void = (e) => {
    console.log(e.target);
    let isDropDownItem = false;
    document.querySelectorAll(".menu-item").forEach((item) => {
      if (e.target === item) isDropDownItem = true;
    });
    document.querySelectorAll(".slider-item").forEach((item) => {
      if (e.target === item) isDropDownItem = true;
    });
    document.querySelectorAll(".slider").forEach((item) => {
      if (e.target === item) isDropDownItem = true;
    });
    if (!isDropDownItem) setOpen(!open);
  };

  useEffect(() => {
    // if (open) document.addEventListener("click", handleWindowClick, true);
    // else document.removeEventListener("click", handleWindowClick, true);
    console.log("opened");
  }, [open]);

  return (
    <li
      className={
        "nav-item " +
        props.className +
        " " +
        (props.shouldGreyOut && props.isVisualized ? " greyed-out" : "")
      }
    >
      <p
        className="icon-button"
        onClick={() => {
          if (!props.shouldGreyOut || !props.isVisualized) setOpen(!open);
        }}
      >
        {props.text}
      </p>
      {open && props.children}
    </li>
  );
};

interface DropDownMenu {
  left: boolean;
}

export const DropDownMenu: React.FC<DropDownMenu> = (props) => {
  const [activeMenu, setActiveMenu] = useState("main");
  return (
    <div className={"dropdown" + (props.left ? " left" : " right")}>
      <CSSTransition
        in={activeMenu === "main"}
        unmountOnExit
        timeout={100}
        classNames="menu-primary"
      >
        {props.children}
      </CSSTransition>
    </div>
  );
};

interface DropDownAlgoProps {
  leftIcon?: string;
  rightIcon?: string;
  algorithmName: string;
  changeAlgorithm: (algorithmName: string) => void;
}

export const DropDownAlgo: React.FC<DropDownAlgoProps> = (props) => {
  return (
    <div
      className="menu-item"
      onClick={(e) => {
        props.changeAlgorithm(props.algorithmName);
      }}
    >
      {props.children}
    </div>
  );
};

interface DropDownSliderProps {
  isVisualized: boolean;
  minValue: number;
  maxValue: number;
  defaultValue: number;
  text: string;
  handleChangeWallsDensity: (event: any) => void;
}

export const DropDownSlider: React.FC<DropDownSliderProps> = (props) => {
  return (
    <div className="slider-item">
      <div>
        <p>{props.text}</p>
      </div>
      <input
        className="slider"
        type="range"
        value={props.defaultValue}
        min={props.minValue.toString()}
        max={props.maxValue.toString()}
        step={0.01}
        onChange={(e) => props.handleChangeWallsDensity(e.target.value)}
      ></input>
    </div>
  );
};
