import { node } from "../../usefulInterfaces";
import { dijkstraMult } from "../singleTarget/dijkstra";

export const greedy: (
  grid: node[][],
  pairGrid: [number, number][][],
  mazeGraph: Map<[number, number], [[number, number], number][]>,
  startNode: node,
  targetList: node[]
) => [node[], node[]][] = (
  grid,
  pairGrid,
  mazeGraph,
  startNode,
  targetList
) => {
  let currentStartNode: node = startNode;
  let allVisitedAndPaths: [node[], node[]][] = [];
  while (targetList.length > 0) {
    let [visited, path, endNode] = dijkstraMult(
      grid,
      pairGrid,
      mazeGraph,
      currentStartNode,
      targetList
    );
    allVisitedAndPaths.push([visited, path]);
  }
  return allVisitedAndPaths;
};
